import { useHistory } from 'react-router-dom';

const Debits = (props) => {
  const { debits, addDebit } = props;
  const history = useHistory();

  const handleAddDebit = (event) => {
    event.preventDefault();
    const description = event.target.elements.description.value;
    const amount = parseFloat(event.target.elements.amount.value);

    if (description && !isNaN(amount)) {
      const newDebit = {
        id: Math.floor(Math.random() * 1000),
        description: description,
        amount: amount,
        date: new Date().toISOString(),
      };

      addDebit(newDebit);

      event.target.reset();
    }
  };

  const renderDebits = () => {
    return debits.map((debit) => {
      const formattedDateTime = new Date(debit.date).toISOString();
      return (
        <div className="debit-item" key={debit.id}>
          <div className="debit-info">
            <div>
              <span className="debit-amount">{debit.amount}</span>
            </div>
            <div>
              <span className="debit-description">{debit.description}</span>
            </div>
            <div className="debit-time">{formattedDateTime}</div>
          </div>
        </div>
      );
    });
  };

  const debitAmount = debits.reduce((total, debit) => total + debit.amount, 0);

  const returnToHome = () => {
    history.push('/');
  };

  return (
    <div className="debit-container">
      <div className="debit-header">
        <h1>Debits</h1>
        <p>Total Debit Amount: {debitAmount.toFixed(2)}</p>
      </div>
      <div className="debit-content">
        <div className="debit-form">
          <h2>Add Debit</h2>
          <form onSubmit={handleAddDebit}>
            <input
              type="text"
              name="description"
              placeholder="Description"
              className="debit-description-bar"
            />
            <input
              type="number"
              name="amount"
              placeholder="Amount"
              className="debit-amount-bar"
            />
            <button type="submit" className="add-debit">
              Add Debit
            </button>
          </form>
        </div>
        <div className="debit-list">
          <ul>{renderDebits()}</ul>
        </div>
      </div>
      <div className="return-home">
        <button className="debit-return" onClick={returnToHome}>
          Return to Home
        </button>
      </div>
    </div>
  );
};

export default Debits;
